sp_widget.script:
(function() {
	var user = gs.getUser();	

	data.isLoggedIn = gs.getSession().isLoggedIn();
	data.userName = user.getDisplayName();

	// if a user has multiple roles, the one with
	// the highest privilege level is chosen
	data.userRole = null;
	if (user.hasRole('x_naci_future_fell.admin'))
		data.userRole = 'admin';
	else if (user.hasRole('x_naci_future_fell.user'))
		data.userRole = 'user';
	else if (user.hasRole('x_naci_future_fell.applicant'))
		data.userRole = 'applicant';

	data.links = [];

	if (data.userRole === 'admin') {
		data.links = [
			{
				icon: 'fa fa-search fa-2x',
				title: 'Search Applicants',
				link: 'search'
			},

			{
				icon: 'fa fa-user fa-2x',
				title: 'New Applicant',
				link: 'new-applicant'
			},

			{
				icon: 'fa fa-users fa-2x',
				title: 'Saved Applicants',
				link: 'saved-applicants'
			},

			{
				icon: 'fa fa-history fa-2x',
				title: 'Track Users',
				link: 'track-users'
			},

			{
				icon: 'fa fa-sliders fa-2x',
				title: 'Update Preferences',
				link: 'preferences'
			}
		];
	} else if (data.userRole === 'user') {
		data.links = [
			{
				icon: 'fa fa-search fa-2x',
				title: 'Search Applicants',
				link: 'search'
			},

			{
				icon: 'fa fa-user fa-2x',
				title: 'New Applicants',
				link: 'new-applicant'
			},

			{
				icon: 'fa fa-users fa-2x',
				title: 'Saved Applicants',
				link: 'saved-applicants'
			},

			{
				icon: 'fa fa-sliders fa-2x',
				title: 'Update Preferences',
				link: 'preferences'
			}
		];
	} else if (data.userRole === 'applicant') {
		data.links = [
			{
				icon: 'fa  fa-pencil-square-o fa-2x',
				title: 'Update My Application',
				link: 'applicant'
			},

			{
				icon: 'fa fa-list-alt fa-2x',
				title: 'View My Application Status',
				link: 'application-status'
			}
		]	
	}


//	$sp.log("Testing..." + $sp.getParameter('wow'));
})();
