sp_widget.client_script:
function($scope, $timeout, $location, spUtil) {
  var c = this;

	/* make navbar sticky */
		$timeout(function() {
			$('#main-navigation')
				.addClass('navbar-sticky')
				.insertAfter('header');
		})

	$scope.$on('$locationChangeStart', function(e) {
		c.loading = true;
	});

	$scope.$on('$locationChangeSuccess', function(e) {
		c.loading = false;
	});
}

sp_widget.script:
(function() {
	var user = gs.getUser();

	data.defaultIDP = gs.getProperty('glide.authenticate.sso.redirect.idp', '14a8eb8cdb5b320054d8ff621f9619d3');
	data.isLoggedIn = gs.getSession().isLoggedIn();
	data.userName = user.getDisplayName();
	data.userRole = ClientUtils.getRole();

	// initialize navbar links
	if (!data.isLoggedIn) {
		// links for unauthenticated users
		data.pageLinks = [
			{title: 'Home', name: 'home'},
			{title: 'Apply', name: 'apply'}
		];
	} else {
		// default links for regular users
		data.pageLinks = [
			{
				title: 'Home',
				name: 'home'
			},
			{
				title: 'Search Applicants',
				name: 'search'
			},

			{
				title: 'New Applicant',
				name: 'new-applicant'
			},

			{
				title: 'Saved Applicants',
				name: 'saved-applicants'
			},

			{
				title: 'Update Preferences',
				name: 'preferences'
			}
		];

		// links for administrators
		if (data.userRole === 'admin') {
			data.pageLinks.push({
				title: 'Track Users',
				name: 'track-users'
			});

		// links for applicants
		} else if (data.userRole === 'applicant') {
			data.pageLinks = [
				{title: 'Home', name: 'home'},
				{title: 'Update Application', name: 'update-application'},
				{title: 'Application Status', name: 'application-status'}
			];
		}
	}
})();
